import { Component } from '@angular/core';
import { RouterLink } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [RouterLink],
  template: `
    <section class="hero">
      <div class="container hero-inner">
        <div class="copy">
          <h1>Private, elevated home cleaning<br><span class="accent">for clients who value discretion.</span></h1>
          <p>Calm, professional service. White-glove standards. Quiet efficiency.</p>
          <div class="buttons"><a class="btn btn--primary" routerLink="/auth/register">Request a Quote</a></div>
        </div>
        <div class="card">
          <h3>Limited weekly openings</h3>
          <form>
            <label class="field"><span>Name</span><input type="text" placeholder="Your name" /></label>
            <label class="field"><span>City</span><input type="text" placeholder="Tucson / Oro Valley" /></label>
            <label class="field"><span>Sq Ft</span><input type="text" placeholder="e.g., 2050" /></label>
            <button class="btn btn--primary" style="width:100%">Request Quote</button>
          </form>
        </div>
      </div>
    </section>
    <section class="section section--light">
      <div class="container">
        <h2 style="text-align:center;margin-bottom:2rem">Signature Services</h2>
        <div class="services">
          <div class="service"><h3>The Private Reset</h3><p class="muted">Full-home refresh for kitchens, bathrooms, floors.</p></div>
          <div class="service"><h3>White-Glove Deep Clean</h3><p class="muted">Baseboards, fan blades, fixtures, cabinets.</p></div>
          <div class="service"><h3>Move-In / Move-Out</h3><p class="muted">Inspection-ready cleaning for turnover.</p></div>
          <div class="service"><h3>Executive Maintenance</h3><p class="muted">Weekly or biweekly recurring service.</p></div>
        </div>
      </div>
    </section>
  `,
  styles: [`
    .hero { padding: 4rem 0; background: linear-gradient(135deg, #faf9f7, #f5f3ef); }
    .hero-inner { display: grid; grid-template-columns: 1fr 380px; gap: 3rem; align-items: center; }
    h1 { font-size: 2.5rem; line-height: 1.2; margin-bottom: 1rem; }
    .accent { color: #c9a962; }
    .copy p { color: #6b6b6b; margin-bottom: 1.5rem; }
    .buttons { display: flex; gap: 1rem; }
    .services { display: grid; grid-template-columns: repeat(4, 1fr); gap: 1.5rem; }
    .service { background: #fff; padding: 1.5rem; border-radius: 8px; border: 1px solid #e5e3df; }
    .service h3 { font-size: 1.1rem; margin-bottom: 0.5rem; }
    @media (max-width: 900px) { .hero-inner { grid-template-columns: 1fr; } .services { grid-template-columns: 1fr 1fr; } }
    @media (max-width: 600px) { .services { grid-template-columns: 1fr; } }
  `]
})
export class HomeComponent {}
