import { Component } from '@angular/core';
import { RouterLink, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { AuthService } from '../../../core/services/auth.service';
import { environment } from '../../../../environments/environment';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [RouterLink, CommonModule, FormsModule],
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {
  registerForm = {
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    agreeTerms: false,
    ageConfirm: false
  };

  isSubmitting = false;
  errorMessage = '';
  successMessage = '';

  constructor(
    private http: HttpClient,
    private authService: AuthService,
    private router: Router
  ) { }

  onSubmit() {
    this.errorMessage = '';

    if (!this.validateForm()) {
      return;
    }

    this.isSubmitting = true;

    const payload = {
      firstName: this.registerForm.firstName,
      lastName: this.registerForm.lastName,
      email: this.registerForm.email,
      phone: this.registerForm.phone,
      password: this.registerForm.password
    };

    this.http.post<any>(`${environment.apiUrl}/auth/register`, payload)
      .subscribe({
        next: (response) => {
          this.isSubmitting = false;
          this.successMessage = 'Account created successfully! Redirecting...';
          setTimeout(() => {
            this.router.navigate(['/auth/login']);
          }, 2000);
        },
        error: (err) => {
          this.isSubmitting = false;
          this.errorMessage = err.error?.message || 'Registration failed. Please try again.';
        }
      });
  }

  validateForm(): boolean {
    if (!this.registerForm.firstName || !this.registerForm.lastName) {
      this.errorMessage = 'Please enter your full name.';
      return false;
    }

    if (!this.registerForm.email) {
      this.errorMessage = 'Please enter a valid email address.';
      return false;
    }

    if (!this.registerForm.phone) {
      this.errorMessage = 'Please enter your phone number.';
      return false;
    }

    if (this.registerForm.password.length < 8) {
      this.errorMessage = 'Password must be at least 8 characters.';
      return false;
    }

    if (this.registerForm.password !== this.registerForm.confirmPassword) {
      this.errorMessage = 'Passwords do not match.';
      return false;
    }

    if (!this.registerForm.ageConfirm) {
      this.errorMessage = 'You must confirm you are 21 or older.';
      return false;
    }

    if (!this.registerForm.agreeTerms) {
      this.errorMessage = 'You must agree to the terms and conditions.';
      return false;
    }

    return true;
  }
}
<section class="auth-section">
  <div class="container">
    <div class="auth-card">
      <div class="auth-header">
        <h1>Create Account</h1>
        <p>Join Clean Encounters for exclusive access</p>
      </div>

      <form (ngSubmit)="onSubmit()" class="auth-form" *ngIf="!successMessage">
        <div class="form-row">
          <div class="form-group">
            <label for="firstName">First Name</label>
            <input 
              type="text" 
              id="firstName" 
              [(ngModel)]="registerForm.firstName" 
              name="firstName" 
              placeholder="First name"
              required>
          </div>
          <div class="form-group">
            <label for="lastName">Last Name</label>
            <input 
              type="text" 
              id="lastName" 
              [(ngModel)]="registerForm.lastName" 
              name="lastName" 
              placeholder="Last name"
              required>
          </div>
        </div>

        <div class="form-group">
          <label for="email">Email Address</label>
          <input 
            type="email" 
            id="email" 
            [(ngModel)]="registerForm.email" 
            name="email" 
            placeholder="your@email.com"
            required>
        </div>

        <div class="form-group">
          <label for="phone">Phone Number</label>
          <input 
            type="tel" 
            id="phone" 
            [(ngModel)]="registerForm.phone" 
            name="phone" 
            placeholder="(555) 123-4567"
            required>
        </div>

        <div class="form-group">
          <label for="password">Password</label>
          <input 
            type="password" 
            id="password" 
            [(ngModel)]="registerForm.password" 
            name="password" 
            placeholder="At least 8 characters"
            required>
        </div>

        <div class="form-group">
          <label for="confirmPassword">Confirm Password</label>
          <input 
            type="password" 
            id="confirmPassword" 
            [(ngModel)]="registerForm.confirmPassword" 
            name="confirmPassword" 
            placeholder="Confirm your password"
            required>
        </div>

        <div class="checkbox-group">
          <label class="checkbox-label">
            <input type="checkbox" [(ngModel)]="registerForm.ageConfirm" name="ageConfirm">
            <span class="checkmark"></span>
            <span class="checkbox-text">I confirm that I am 21 years of age or older</span>
          </label>
        </div>

        <div class="checkbox-group">
          <label class="checkbox-label">
            <input type="checkbox" [(ngModel)]="registerForm.agreeTerms" name="agreeTerms">
            <span class="checkmark"></span>
            <span class="checkbox-text">I agree to the <a routerLink="/terms">Terms of Service</a> and <a routerLink="/privacy">Privacy Policy</a></span>
          </label>
        </div>

        <div class="error-message" *ngIf="errorMessage">{{ errorMessage }}</div>

        <button type="submit" class="btn btn--gold" [disabled]="isSubmitting">
          <span *ngIf="!isSubmitting">Create Account</span>
          <span *ngIf="isSubmitting">Creating account...</span>
        </button>
      </form>

      <div class="success-message" *ngIf="successMessage">
        <div class="success-icon">✓</div>
        <p>{{ successMessage }}</p>
      </div>

      <div class="auth-footer" *ngIf="!successMessage">
        <p>Already have an account? <a routerLink="/auth/login">Sign in</a></p>
      </div>
    </div>
  </div>
</section>
