import { Component, inject } from '@angular/core';
import { RouterLink } from '@angular/router';
import { AuthService } from '../../../core/services/auth.service';

@Component({
  selector: 'app-header',
  standalone: true,
  imports: [RouterLink],
  template: `
    <div class="topbar"><div class="container"><span class="pill">21+ Scheduling</span> <a href="tel:+15551234567">(555) 123-4567</a></div></div>
    <header class="header">
      <div class="container header-inner">
        <a class="brand" routerLink="/"><span class="mark">CE</span><span class="text">Clean Encounters</span></a>
        <nav><a routerLink="/">Home</a><a routerLink="/services">Services</a><a routerLink="/about">About</a></nav>
        <div class="cta">
          @if (auth.isLoggedIn()) { <a class="btn btn--primary" routerLink="/portal">My Account</a> }
          @else { <a class="btn btn--primary" routerLink="/auth/register">Get Started</a> }
        </div>
      </div>
    </header>
  `,
  styles: [`
    .topbar { background: #1a1a1a; color: #fff; font-size: 0.75rem; padding: 0.5rem 0; display: flex; justify-content: space-between; }
    .topbar a { color: #fff; } .pill { background: #c9a962; padding: 0.2rem 0.6rem; border-radius: 20px; }
    .header { background: #faf9f7; padding: 1rem 0; border-bottom: 1px solid #e5e3df; position: sticky; top: 0; z-index: 100; }
    .header-inner { display: flex; align-items: center; justify-content: space-between; }
    .brand { display: flex; align-items: center; gap: 0.5rem; font-family: 'Cormorant Garamond', serif; font-size: 1.25rem; }
    .mark { background: #1a1a1a; color: #fff; padding: 0.5rem 0.75rem; border-radius: 4px; }
    nav { display: flex; gap: 2rem; } nav a { font-size: 0.875rem; }
    .cta { display: flex; gap: 0.5rem; }
  `]
})
export class HeaderComponent { auth = inject(AuthService); }
